mapper.select:
select tmp.* ,if(tmp1.visit_count is null ,0,tmp1.visit_count) visit_count from( SELECT sum( CASE WHEN ae.lated_for_time > 0 THEN 1 WHEN ae.lated_for_time IS NULL THEN 0 ELSE 0 END )lateSum, sum( CASE WHEN ae.le_for_time > 0 THEN 1 WHEN ae.le_for_time IS NULL THEN 0 ELSE 0 END )AS leSum, sum( CASE WHEN ae.state = 2 THEN 1 ELSE 0 END )AS norSignIn, sum( CASE WHEN ae.state = 1 THEN 1 ELSE 0 END )AS norSignOut, ae.updator, ae.user_id FROM ( SELECT aes.* FROM attence_events aes, users u WHERE aes.user_id = u.user_id AND u.dept_id = #{departmentId,jdbcType=INTEGER} )AS ae WHERE ae.update_time BETWEEN #{start,jdbcType=TIMESTAMP} AND #{end,jdbcType=TIMESTAMP} GROUP BY ae.user_id order by ae.user_id) tmp left join (
SELECT count(1) AS visit_count, ves.user_id FROM visit_events ves, users u WHERE ves.user_id = u.user_id AND u.dept_id = #{departmentId,jdbcType=INTEGER}
and ves.update_time BETWEEN #{start,jdbcType=TIMESTAMP} AND #{end,jdbcType=TIMESTAMP} GROUP BY ves.user_id ORDER BY ves.user_id
) tmp1 on tmp.user_id = tmp1.user_id
